def mult_cipher(cipher, mult, e, n):
	cipher_int = int.from_bytes(cipher, 'big')
	mult_cipher_int = (cipher_int * pow(mult, e, n)) % n
	ans_size = math.ceil(math.log(mult_cipher_int, 16))
	return mult_cipher_int.to_bytes(ans_size, 'big').lstrip(b'\x00')

